#mulchcli/mulchcli/commands/config.pyimport clickfrom pathlib import Path@click.command("config")@click.argument("project_name")@click.argument("key")@click.argument("value")def config_command(project_name, key, value):    """Add or update a key in the config.toml for a given project."""    config_path = Path(f"projects/{project_name}/configs/config.toml")    config_path.parent.mkdir(parents=True, exist_ok=True)    with config_path.open("a") as f:        f.write(f'{key} = "{value}"\n')    click.echo(f"[?] Set {key} = {value} in {config_path}")