import clickimport os@click.command("init")@click.argument("project_name")def init_command(project_name):    """Initialize a new project scaffold."""    base = f"projects/{project_name}"    structure = [        "configs", "db", "docs", "exports",        "groupings", "imports", "scripts"    ]    for folder in structure:        os.makedirs(os.path.join(base, folder), exist_ok=True)    click.echo(f"[?] Initialized project: {project_name}")