# mulchcli/mulchcli/commands/list_projects.pyimport clickimport os@click.command("list-projects")def list_projects_command():    """List all available projects."""    base_path = "projects"    if not os.path.exists(base_path):        click.echo("No projects directory found.")        return    projects = [        name for name in os.listdir(base_path)        if os.path.isdir(os.path.join(base_path, name))    ]    if not projects:        click.echo("No projects found.")    else:        click.echo("?? Available projects:")        for proj in projects:            click.echo(f" - {proj}")